import React from 'react';
import {
  FlatList,
  ScrollView,
  Text,
  View,
  Image,
  Dimensions,
  TouchableOpacity,
  SafeAreaView,
  TextInput,
  StatusBar,
  Platform,
} from 'react-native';

import Carousel from 'react-native-snap-carousel';
import {white} from '../../../assets/colors/index';

import styles from './styles';

export default class HomeScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,
      searchText: '',
      myTrends: [
        {
          image: '../../../assets/images/person.jpg',
        },
        {
          image: '../../../assets/images/person.jpg',
        },
        {
          image: '../../../assets/images/person.jpg',
        },
        {
          image: '../../../assets/images/person.jpg',
        },
        {
          image: '../../../assets/images/person.jpg',
        },
        {
          image: '../../../assets/images/person.jpg',
        },
      ],
      AllTrends: [
        {
          name: 'Trend Title 1',

          items: [
            {
              image: '../../../assets/images/person.jpg',
            },
            {
              image: '../../../assets/images/person.jpg',
            },
            {
              image: '../../../assets/images/person.jpg',
            },
            {
              image: '../../../assets/images/person.jpg',
            },
          ],
        },
        {
          name: 'Trend Title 2',

          items: [
            {
              image: '../../../assets/images/person.jpg',
            },
            {
              image: '../../../assets/images/person.jpg',
            },
            {
              image: '../../../assets/images/person.jpg',
            },
            {
              image: '../../../assets/images/person.jpg',
            },
          ],
        },
      ],
    };
  }

  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  renderSwiper = ({item, index}) => {
    return (
      <TouchableOpacity
        // onPress={() => this.onPressRecipe(item)}
        style={styles.swiperContainer}>
        {item.image != null ? (
          <Image
            style={styles.swiperPhoto}
            //source={{uri: item.image}}
            source={require('../../../assets/images/person.jpg')}
          />
        ) : (
          <Image
            source={require('../../../assets/images/person.jpg')}
            style={styles.swiperPhoto}
          />
        )}
      </TouchableOpacity>
    );
  };

  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  renderAllItems = ({item}) => (
    <View style={{backgroundColor: white, marginBottom: 20}}>
      {item.items != '' ? (
        <View style={{backgroundColor: white}}>
          <TouchableOpacity style={{paddingLeft: 15, flexDirection: 'row'}}>
            <Image
              source={require('../../../assets/images/hashColored.png')}
              style={{width: 25, height: 25, marginRight: 15}}
            />
            <Text style={{color: '#3757f5', fontSize: 16, fontWeight: '700'}}>
              {item.name}
            </Text>
          </TouchableOpacity>
          <ScrollView horizontal={true}>
            {item.items.map((data) => {
              return (
                <TouchableOpacity
                  style={styles.itemContainer}
                  onPress={() => this.props.navigation.navigate('UserProfile')}>
                  {data.image != null ? (
                    <Image
                      // source={{uri: data.image}}
                      source={require('../../../assets/images/person.jpg')}
                      style={styles.itemPhoto}
                    />
                  ) : (
                    <Image
                      source={require('../../../assets/images/person.jpg')}
                      style={[styles.itemPhoto, {borderRadius: 10}]}
                    />
                  )}
                </TouchableOpacity>
              );
            })}
          </ScrollView>
        </View>
      ) : null}
    </View>
  );

  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  render() {
    const Carousel_WIDTH = Dimensions.get('window').width;
    return (
      <View style={{flex: 1}}>
        <View
          style={{
            backgroundColor: '#969696',
            height: Platform.OS === 'ios' ? 35 : StatusBar.currentHeight,
          }}>
          <StatusBar
            translucent
            backgroundColor={'#666'}
            barStyle={'light-content'}
          />
        </View>
        <SafeAreaView style={styles.container}>
          <ScrollView contentContainerStyle={{width: '100%', marginTop: 10}}>
            <View style={{flex: 1}}>
              <TouchableOpacity style={styles.searchBar}>
                <Image
                  source={require('../../../assets/images/search.png')}
                  style={{width: 25, height: 25, tintColor: '#777'}}
                />
                <TextInput
                  style={styles.searchText}
                  placeholder={strings.search}
                  onChangeText={(text) => this.setState({searchText: text})}
                  defaultValue={this.state.searchText}
                />
              </TouchableOpacity>
              <Carousel
                layout={'default'}
                autoplay={true}
                loop={true}
                ref={(ref) => (this.carousel = ref)}
                data={this.state.myTrends}
                sliderWidth={Carousel_WIDTH - 1}
                itemWidth={Carousel_WIDTH - 1}
                renderItem={this.renderSwiper}
                onSnapToItem={(index) => this.setState({activeIndex: index})}
              />
              <FlatList
                style={{flex: 1, marginVertical: 20, backgroundColor: 'white'}}
                data={this.state.AllTrends}
                renderItem={this.renderAllItems}
                keyExtractor={(item) => item.id}
              />
            </View>
          </ScrollView>
        </SafeAreaView>
      </View>
    );
  }
}
