import React from 'react';
import {
  FlatList,
  ScrollView,
  Text,
  View,
  Image,
  TouchableOpacity,
  SafeAreaView,
  StatusBar,
  Platform,
} from 'react-native';
import {white} from '../../../assets/colors/index';

import styles from './styles';

export default class MyProfileScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,

      AllTrends: [
        {
          username: 'Sereen',
          following: '5903',
          followers: '51459',
          likes: '254152',
          views: '173642',
          items: [
            {
              name: 'Trend Title 1',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 2',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 3',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 4',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 5',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 6',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 7',
              image: '../../../assets/images/person.jpg',
            },
            {
              name: 'Trend Title 8',
              image: '../../../assets/images/person.jpg',
            },
          ],
        },
      ],
    };
  }

  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  renderAllItems = ({item}) => (
    <View style={{backgroundColor: white, marginBottom: 20, flex: 1}}>
      {item.items != '' ? (
        <View style={{backgroundColor: white}}>
          <View style={styles.profileInfoContainer}>
            <Image
              source={require('../../../assets/images/person.jpg')}
              style={styles.profileImage}
            />
            <View style={{justifyContent: 'center', alignItems: 'center'}}>
              <Text style={styles.username}>{item.username}</Text>
              <View style={{flexDirection: 'row'}}>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, {fontWeight: 'bold'}]}>
                    {item.following}
                  </Text>
                  <Text style={styles.userInteractionsText}>Following</Text>
                </View>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, {fontWeight: 'bold'}]}>
                    {item.followers}
                  </Text>
                  <Text style={styles.userInteractionsText}>Followers</Text>
                </View>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, {fontWeight: 'bold'}]}>
                    {item.likes}
                  </Text>
                  <Text style={styles.userInteractionsText}>Likes</Text>
                </View>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, {fontWeight: 'bold'}]}>
                    {item.views}
                  </Text>
                  <Text style={styles.userInteractionsText}>Views</Text>
                </View>
              </View>
            </View>
          </View>
          <View style={styles.imagesGroup}>
            {item.items.map((data) => {
              return (
                <TouchableOpacity style={styles.itemContainer}>
                  {data.image != null ? (
                    <View style={styles.itemView}>
                      <Image
                        // source={{uri: data.image}}
                        source={require('../../../assets/images/person.jpg')}
                        style={styles.itemPhoto}
                      />
                      <View style={styles.trendTitleView}>
                        <Image
                          source={require('../../../assets/images/hashColored.png')}
                          style={{width: 15, height: 15, marginRight: 5}}
                        />
                        <Text style={styles.trendTitleText}>{data.name}</Text>
                      </View>
                    </View>
                  ) : (
                    <View style={styles.itemView}>
                      <Image
                        source={require('../../../assets/images/person.jpg')}
                        style={styles.itemPhoto}
                      />
                      <View style={styles.trendTitleView}>
                        <Image
                          source={require('../../../assets/images/hashColored.png')}
                          style={{width: 15, height: 15, marginRight: 5}}
                        />
                        <Text style={styles.trendTitleText}>{data.name}</Text>
                      </View>
                    </View>
                  )}
                </TouchableOpacity>
              );
            })}
          </View>
        </View>
      ) : null}
    </View>
  );

  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  render() {
    return (
      <View style={{flex: 1}}>
        <View
          style={{
            backgroundColor: '#666',
            height: Platform.OS === 'ios' ? 35 : StatusBar.currentHeight,
          }}>
          <StatusBar
            // translucent
            backgroundColor={'#666'}
            barStyle={'light-content'}
          />
        </View>
        <SafeAreaView style={styles.container}>
          <TouchableOpacity
            onPress={() => this.props.navigation.toggleDrawer()}
            style={styles.menuButton}>
            <Image
              source={require('../../../assets/images/menu.png')}
              style={{width: 30, height: 30, tintColor: '#111'}}
              resizeMode={'contain'}
            />
          </TouchableOpacity>
          <ScrollView
            contentContainerStyle={{width: '100%', paddingBottom: 30}}>
            <FlatList
              style={{flex: 1, backgroundColor: 'white'}}
              data={this.state.AllTrends}
              renderItem={this.renderAllItems}
              keyExtractor={(item) => item.id}
            />
          </ScrollView>
        </SafeAreaView>
      </View>
    );
  }
}
