import React from 'react';
import {
  FlatList,
  ScrollView,
  Text,
  View,
  Image,
  TouchableOpacity,
  SafeAreaView,
  StatusBar,
  Platform,
  I18nManager,
} from 'react-native';

import { white } from '../../../assets/colors/index';
import Footer from '../../components/Footer';
import styles from './styles';
import config from '../../../assets/Config';
import AsyncStorage from '@react-native-async-storage/async-storage';

export default class UserProfileScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,
      data: [],
    };
  }
  componentDidMount() {
    this.getAllTrends();
  }

  async getAllTrends() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    console.log(userId);
    this.setState({ showProgress: true });
    try {
      let response = await fetch(config.DOMAIN + 'usertrendinfo', {
        method: 'GET',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
      });
      let res = await response.json();
      console.log('res', response.url);
      console.log('res', res);
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        this.setState({
          showProgress: false,
          data: res.comments,
        });
      }
    } catch (error) {
      console.log(error);
      this.setState({ showProgress: false });
    }
  }
  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  renderAllItems = ({ item }) => (
    <View style={{ backgroundColor: white, marginBottom: 20, flex: 1 }}>
      {item.items != '' ? (
        <View style={{ backgroundColor: white }}>
          <View style={styles.profileInfoContainer}>
            <Image
              source={require('../../../assets/images/person.jpg')}
              style={styles.profileImage}
            />
            <View
              style={{ justifyContent: 'center', alignItems: 'center', flex: 1 }}>
              <Text style={styles.username}>{item.user_name}</Text>
              <View style={{ flexDirection: 'row', marginBottom: 20 }}>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, { fontWeight: 'bold' }]}>
                    {item.following}
                  </Text>
                  <Text style={styles.userInteractionsText}>Following</Text>
                </View>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, { fontWeight: 'bold' }]}>
                    {item.followers}
                  </Text>
                  <Text style={styles.userInteractionsText}>Followers</Text>
                </View>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, { fontWeight: 'bold' }]}>
                    {item.likes}
                  </Text>
                  <Text style={styles.userInteractionsText}>Likes</Text>
                </View>
                <View style={styles.userInteractions}>
                  <Text
                    style={[styles.userInteractionsText, { fontWeight: 'bold' }]}>
                    {item.views}
                  </Text>
                  <Text style={styles.userInteractionsText}>Views</Text>
                </View>
              </View>
              <TouchableOpacity style={styles.followButton}>
                <Text style={styles.followText}>Follow</Text>
              </TouchableOpacity>
            </View>
          </View>
          <View style={styles.imagesGroup}>
            {item.user_trend.map((data) => {
              return (
                <TouchableOpacity style={styles.itemContainer}>
                  {data.cover_image != null ? (
                    <View style={styles.itemView}>
                      <Image
                        source={{ uri: config.IMG_DOMAIN + data.cover_image }}
                        // source={require('../../../assets/images/person.jpg')}
                        style={styles.itemPhoto}
                      />
                      <View style={styles.trendTitleView}>
                        <Image
                          source={require('../../../assets/images/hashColored.png')}
                          style={{ width: 15, height: 15, marginRight: 5 }}
                        />
                        <Text style={styles.trendTitleText}>{data.title}</Text>
                      </View>
                    </View>
                  ) : (
                      <View style={styles.itemView}>
                        <Image
                          source={require('../../../assets/images/person.jpg')}
                          style={styles.itemPhoto}
                        />
                        <View style={styles.trendTitleView}>
                          <Image
                            source={require('../../../assets/images/hashColored.png')}
                            style={{ width: 15, height: 15, marginRight: 5 }}
                          />
                          <Text style={styles.trendTitleText}>{data.title}</Text>
                        </View>
                      </View>
                    )}
                </TouchableOpacity>
              );
            })}
          </View>
        </View>
      ) : null}
    </View>
  );

  ///////////////////////////////////////////////////////////////////////////////////////////////////
  ///////////////////////////////////////////////////////////////////////////////////////////////////

  render() {
    return (
      <View style={{ flex: 1 }}>
        <ScrollView>
          <View
            style={{
              backgroundColor: '#666',
              height: Platform.OS === 'ios' ? 35 : StatusBar.currentHeight,
            }}>
            <StatusBar translucent backgroundColor={'#666'} />
          </View>
          <SafeAreaView style={styles.container}>
            <TouchableOpacity
              onPress={() => this.props.navigation.toggleDrawer()}
              style={styles.backButton}>
              <Image
                source={require('../../../assets/images/arrow.png')}
                style={{
                  width: 25,
                  height: 25,
                  tintColor: '#111',
                  transform: [{ scaleX: I18nManager.isRTL ? 1 : -1 }],
                }}
                resizeMode={'contain'}
              />
            </TouchableOpacity>
            <ScrollView
              contentContainerStyle={{ width: '100%', paddingBottom: 30 }}>
              <FlatList
                style={{ flex: 1, backgroundColor: 'white' }}
                data={this.state.data}
                renderItem={this.renderAllItems}
                keyExtractor={(item) => item.id}
              />
            </ScrollView>
          </SafeAreaView>
        </ScrollView>

        <View>
          <Footer nav={this.props.navigation} />
        </View>
      </View>
    );
  }
}
