import React, {Component} from 'react';
import {
  TouchableOpacity,
  View,
  Image,
  Text,
  Dimensions,
  ToastAndroid,
  Platform,
  AlertIOS,
  Modal,
  Linking,
  StyleSheet,
  Animated,
  PanResponder,
  TextInput,
  ScrollView,
} from 'react-native';
export const {width, height} = Dimensions.get('window');
import config from '../../assets/Config';
import VideoPlayer from 'react-native-video-custom-controls';
import {Icon} from 'native-base';
import {gray, orange, white, yellow} from '../../assets/colors';
import {LoaderBox} from '../components/LoaderBox';
import AsyncStorage from '@react-native-async-storage/async-storage';
import AnimatedNumbers from 'react-native-animated-numbers';

export default class TrendBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showBtn: false,
      // animateToNumberAdd: 6,
      // animateToNumberMin: 6,
      animateToNumberAdd: this.props.data.trend_like.length,
      animateToNumberMin: this.props.data.trend_dislike.length,
      listOfComments: [],
      isVisibal: false,
      panY: new Animated.Value(height),
      like: false,
      dislike: false,
      favCount: false,
    };
    this._resetPositionAnim = Animated.timing(this.state.panY, {
      toValue: 0,
      duration: 300,
    });
    this._closeAnim = Animated.timing(this.state.panY, {
      toValue: Dimensions.get('screen').height,
      duration: 500,
    });
    this._panResponders = PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onMoveShouldSetPanResponder: () => false,
      onPanResponderMove: Animated.event([null, {dy: this.state.panY}]),
      onPanResponderRelease: (e, gs) => {
        if (gs.dy > 0 && gs.vy > 2) {
          return this._closeAnim.start(() => this.props.onDismiss());
        }
        return this._resetPositionAnim.start();
      },
    });
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevState.isVisibal !== this.state.isVisibal && this.state.isVisibal) {
      this._resetPositionAnim.start();
    }
  }
  async getComments() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    this.setState({showProgress: true});
    try {
      let response = await fetch(config.DOMAIN + 'getcommentbytrend', {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trend_id: this.props.data.id,
        }),
      });
      let res = await response.json();
      console.log('res', response.url);
      console.log('res', res);
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        this.setState({showProgress: false, listOfComments: res.comments});
      }
    } catch (error) {
      this.error(error);
      this.setState({showProgress: false});
    }
  }
  async componentDidMount() {
    this.getComments();
  }
  _handleDismiss() {
    this._closeAnim.start(() => this.props.onDismiss());
  }
  renderLoading() {
    if (this.state.showProgress) {
      return <LoaderBox />;
    }
  }
  error(msg) {
    if (Platform.OS === 'android') {
      ToastAndroid.show(msg, ToastAndroid.LONG);
    } else {
      AlertIOS.alert(msg);
    }
  }
  async goToUserProfile() {
    this.props.nav.navigate('UserProfile');
  }

  async disLike() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    this.setState({showProgress: true});
    try {
      let response = await fetch(config.DOMAIN + 'adddisliketrend', {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trend_id: this.props.data.id,
        }),
      });
      let res = await response.json();
      console.log('tttttttttttt', this.props.data.id);
      console.log('res', response.url);
      console.log('res', res);
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        this.setState({showProgress: false});
      }
    } catch (error) {
      this.error(error);
      this.setState({showProgress: false});
    }
  }

  async like() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    this.setState({showProgress: true});
    try {
      let response = await fetch(config.DOMAIN + 'addliketrend', {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trend_id: this.props.data.id,
        }),
      });
      let res = await response.json();
      console.log('tttttttttttt', this.props.data.id);

      console.log('res', response.url);
      console.log('res', res);
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        this.setState({showProgress: false});
      }
    } catch (error) {
      this.error(error);
      this.setState({showProgress: false});
    }
  }
  async addComment() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    this.setState({showProgress: true});
    try {
      let response = await fetch(config.DOMAIN + 'addcomment', {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trend_id: this.props.data.id,
          comment_txt: this.state.comment,
        }),
      });
      let res = await response.json();
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        this.getComments();
        this.setState({showProgress: false, comment: ''});
      }
    } catch (error) {
      this.error(error);
      this.setState({showProgress: false});
    }
  }
  setModalStatus() {
    this.setState({
      isVisibal: !this.state.isVisibal,
    });
  }
  async shareTrend() {}

  async addToFav() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    this.setState({showProgress: true});
    try {
      let response = await fetch(config.DOMAIN + 'addfavouritetrend', {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trend_id: this.props.data.id,
        }),
      });
      let res = await response.json();
      console.log('tttttttttttt', this.props.data.id);
      console.log('res', response.url);
      console.log('res', res);
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        this.setState({showProgress: false, favCount: !this.state.favCount});
        this.error(
          !this.state.favCount ? 'Removed From Fav !' : 'Added To Fav !',
        );
      }
    } catch (error) {
      this.error(error);
      this.setState({showProgress: false});
    }
  }
  async downloadTrend() {
    const userId = await AsyncStorage.getItem('@Trend:token');
    this.setState({showProgress: true});
    try {
      let response = await fetch(config.DOMAIN + 'link', {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + userId,
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          trend_id: this.props.data.id,
        }),
      });
      let res = await response.json();
      console.log('tttttttttttt', this.props.data.id);
      console.log('res', response.url);
      console.log('res', res.link);
      if (res.status == 400) {
        this.error(res.messg);
      } else {
        Linking.openURL(res.link);
        this.setState({showProgress: false});
      }
    } catch (error) {
      this.error(error);
      this.setState({showProgress: false});
    }
  }

  render() {
    console.log(this.props.data.user_comment);
    const top = this.state.panY.interpolate({
      inputRange: [-1, 0, 1],
      outputRange: [0, 0, 1],
    });
    var list = [];
    if (this.state.listOfComments != undefined)
      if (this.state.listOfComments.length > 0) {
        list = this.state.listOfComments.map((val, key) => {
          return (
            <View
              key={key}
              style={{
                flexDirection: 'row',
                width: width * 0.9,
                padding: 7,
                margin: 7,
                alignItems: 'center',
                borderRadius: 25,
                shadowColor: '#000',
                shadowOffset: {
                  width: 1,
                  height: 1,
                },
                shadowOpacity: 0.25,
                shadowRadius: 0.84,

                // elevation: 0.005,
              }}>
              <TouchableOpacity
                onPress={() => {
                  this.goToUserProfile();
                  this.setModalStatus();
                }}>
                <Image
                  source={val.image}
                  style={{
                    width: 40,
                    height: 40,
                    marginVertical: 5,
                    borderRadius: 40 / 2,
                    borderWidth: 2,
                  }}
                />
              </TouchableOpacity>
              <View style={{paddingHorizontal: 10}}>
                <Text>{val.name}</Text>
                <Text>{val.comment_txt}</Text>
              </View>
            </View>
          );
        });
      }
    return (
      <>
        <View>
          <VideoPlayer
            source={{
              uri:
                'http://nextstageksa.com/trendapp/storage/trendVideos/1612685528.mp4',
            }}
            navigator={this.props.navigator}
            style={{
              flex: 1,
              height: height * 0.8,
              width: width,
            }}
            seekColor={gray}
            disableVolume
          />

          <View
            style={{
              backgroundColor: 'rgba(0,0,0,0.5)',
              position: 'absolute',
              bottom: 100,
            }}>
            {/* Trend Title & Trend Desc & Action Btn */}
            <View style={{width: '100%', flexDirection: 'row'}}>
              <View
                style={{
                  padding: 10,
                  width: '80%',
                }}>
                <Text style={{color: '#fff'}}>{this.props.data.title}</Text>
                <Text style={{color: '#fff'}}>
                  {this.props.data.trend_desc}
                </Text>
              </View>
              <View
                style={{
                  width: '20%',
                  justifyContent: 'center',
                }}>
                {this.state.showBtn && (
                  <View
                    style={{
                      alignSelf: 'center',
                    }}>
                    <TouchableOpacity onPress={() => this.goToUserProfile()}>
                      <Image
                        source={{
                          uri:
                            config.IMG_DOMAIN +
                            this.props.data.user_trend.image,
                        }}
                        style={{
                          width: 40,
                          height: 40,
                          marginVertical: 5,
                          borderRadius: 40 / 2,
                          borderWidth: 2,
                        }}
                      />
                    </TouchableOpacity>

                    <TouchableOpacity
                      style={{alignItems: 'center', alignContents: 'center'}}
                      onPress={() => {
                        this.disLike();
                        this.setState({
                          animateToNumberMin: this.state.dislike
                            ? this.state.animateToNumberMin - 1
                            : this.state.animateToNumberMin + 1,
                          dislike: !this.state.dislike,
                        });
                      }}>
                      <Image
                        source={
                          this.state.dislike
                            ? require('../../assets/images/dislikeDone.png')
                            : require('../../assets/images/dislike.png')
                        }
                        style={{width: 40, height: 40, marginVertical: 5}}
                      />
                      <AnimatedNumbers
                        includeComma
                        animateToNumber={this.state.animateToNumberMin}
                        fontStyle={{fontSize: 15, color: white}}
                      />
                    </TouchableOpacity>

                    <TouchableOpacity
                      style={{alignItems: 'center', alignContents: 'center'}}
                      onPress={() => {
                        this.like();
                        this.setState({
                          animateToNumberAdd: !this.state.like
                            ? this.state.animateToNumberAdd + 1
                            : this.state.animateToNumberAdd - 1,
                          like: !this.state.like,
                        });
                      }}>
                      <Image
                        source={
                          this.state.like
                            ? require('../../assets/images/likeDone.png')
                            : require('../../assets/images/like.png')
                        }
                        style={{width: 40, height: 40, marginVertical: 5}}
                      />
                      <AnimatedNumbers
                        includeComma
                        animateToNumber={this.state.animateToNumberAdd}
                        fontStyle={{fontSize: 15, color: white}}
                      />
                    </TouchableOpacity>

                    <TouchableOpacity
                      style={{alignItems: 'center', alignContents: 'center'}}
                      onPress={() => {
                        this.setModalStatus();
                      }}>
                      <Image
                        source={require('../../assets/images/comment.png')}
                        style={{width: 40, height: 40, marginVertical: 5}}
                      />
                      <AnimatedNumbers
                        includeComma
                        animateToNumber={this.state.listOfComments.length}
                        fontStyle={{fontSize: 15, color: white}}
                      />
                    </TouchableOpacity>
                  </View>
                )}
                <TouchableOpacity
                  onPress={() => {
                    this.setState({
                      showBtn: !this.state.showBtn,
                    });
                  }}>
                  <Icon
                    type="Ionicons"
                    name="md-ellipsis-horizontal-circle-sharp"
                    style={{
                      color: '#fff',
                      // width: '20%',
                      alignSelf: 'center',
                      fontSize: 45,
                    }}
                  />
                </TouchableOpacity>
              </View>
            </View>

            {this.state.showBtn && (
              <View
                style={{
                  flexDirection: 'row',
                  justifyContent: 'flex-end',
                  width: '70%',
                  alignSelf: 'center',
                }}>
                <TouchableOpacity onPress={() => this.shareTrend()}>
                  <Image
                    source={require('../../assets/images/share.png')}
                    style={{width: 40, height: 40, marginHorizontal: 5}}
                  />
                </TouchableOpacity>
                <TouchableOpacity
                  onPress={() => this.props.nav.navigate('ReportAProblem')}>
                  <Image
                    source={require('../../assets/images/reperot.png')}
                    style={{width: 40, height: 40, marginHorizontal: 5}}
                  />
                </TouchableOpacity>
                <TouchableOpacity onPress={() => this.addToFav()}>
                  <Image
                    source={
                      !this.state.favCount
                        ? require('../../assets/images/fav.png')
                        : require('../../assets/images/favDone.png')
                    }
                    style={{width: 40, height: 40, marginHorizontal: 5}}
                  />
                </TouchableOpacity>
                <TouchableOpacity onPress={() => this.downloadTrend()}>
                  <Image
                    source={require('../../assets/images/download.png')}
                    style={{width: 40, height: 40, marginHorizontal: 5}}
                  />
                </TouchableOpacity>
              </View>
            )}
          </View>
        </View>
        <View style={styles.centeredView}>
          <Modal
            animationType="slide"
            transparent={true}
            visible={this.state.isVisibal}
            onRequestClose={() => {
              this.setModalStatus();
            }}>
            <Animated.View style={[styles.centeredView, {top}]}>
              <View style={styles.modalView}>
                <View
                  style={{
                    flexDirection: 'row',
                    width: width * 0.9,
                    borderBottomColor: orange,
                    borderBottomWidth: 1,
                    padding: 5,
                  }}>
                  <View style={{width: width * 0.07}}>
                    <TouchableOpacity onPress={() => this.setModalStatus()}>
                      <Icon
                        type="AntDesign"
                        name="close"
                        style={{color: orange}}
                      />
                    </TouchableOpacity>
                  </View>
                  <View
                    style={{
                      width: width * 0.8,
                      justifyContent: 'center',
                      alignContent: 'center',
                      alignSelf: 'center',
                    }}>
                    <Text style={styles.modalText}>
                      {'Comments (' + this.state.listOfComments.length + ')'}
                    </Text>
                  </View>
                </View>
                <ScrollView>
                  {list}
                  <View
                    style={{
                      flexDirection: 'row',
                      justifyContent: 'space-between',
                      width: width * 0.9,
                      padding: 7,
                      margin: 7,
                      alignItems: 'center',
                      borderRadius: 25,
                      shadowColor: '#000',
                      shadowOffset: {
                        width: 1,
                        height: 1,
                      },
                      shadowOpacity: 0.25,
                      shadowRadius: 3.84,

                      // elevation: 1,
                    }}>
                    <Image
                      source={require('../../assets/images/person.jpg')}
                      style={{
                        width: 40,
                        height: 40,
                        marginVertical: 5,
                        borderRadius: 40 / 2,
                        borderWidth: 2,
                      }}
                    />
                    <View
                      style={{
                        paddingHorizontal: 10,
                        width: width * 0.7,
                      }}>
                      <Text>{'Islam '}</Text>
                      <TextInput
                        placeholder={'Add Comment '}
                        value={this.state.comment}
                        onChangeText={(text) => this.setState({comment: text})}
                      />
                    </View>
                    <TouchableOpacity onPress={() => this.addComment()}>
                      <Icon
                        type="AntDesign"
                        name="plus"
                        style={{color: orange}}
                      />
                    </TouchableOpacity>
                  </View>
                </ScrollView>
              </View>
            </Animated.View>
          </Modal>
        </View>
      </>
    );
  }
}
