    width: width,
    alignItems: 'center',
  },

  modalText: {
    textAlign: 'center',
    color: orange,
  },
  container: {
    backgroundColor: 'white',
    paddingTop: 12,
    borderTopRightRadius: 12,
    borderTopLeftRadius: 12,
  },
});
