import React, { Component } from 'react';
import {
  TouchableOpacity,
  View,
  Image,
  Text,
  StyleSheet,
  Dimensions,
  Alert,
} from 'react-native';
import config from '../../assets/Config';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { width, height } from '../screens/Map/MapScreen';
import { white } from '../../assets/colors';

export default class InterestBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedFruits: [],
      selected: [],
      sub: [],
    };
  }
  Save(id) {
    var x = this.state.selected
    x.push(id)
    this.state.selected.push(x)
    console.log(this.state.selected)
  }
  render() {
    return (
      <View>
        <TouchableOpacity
          onPress={() => {
            this.setState({
              select: true
            })
          }}>
          <View style={styles.row}>
            <Image
              source={{
                uri: config.IMG_DOMAIN + this.props.data.image,
              }}
              style={{
                height: height * 0.3,
                width: width * 0.98,
                resizeMode: 'cover',
                alignSelf: 'center', borderRadius: 5,
                borderColor: this.state.select ? 'green' : 'red'
              }}
            />
            <Text style={{
              paddingHorizontal: 25, position: 'absolute', color: white, padding: 20, fontWeight: 'bold', backgroundColor: 'rgba(52, 52, 52, 0.2)'
            }}>
              {lang == 'ar' ? this.props.data.name_ar : this.props.data.name_en}
            </Text>
          </View>
        </TouchableOpacity>

      </View>
    );
  }
}
